            <g>
                <image :height="`${imgHeight}px`" :width="`${imgWidth}px`"  xlink:href="../assets/images/bg.jpg"></image>
                <use v-for="item in devArr" :x="item.x" :y="item.y" :xlink:href="item.href" :key="item.href" @mousedown="clickHandle($event, item)"/>
            </g>
