    </div>
</template>

<script>
    export default {
        name: "home",
        data(){
            return {
                curDev:{},
                devX:0,
                devY:0,
                staX:0,
                staY:0,
                buff:false,
                curX:0,
                curY:0,
                imgWidth: 1920,
                imgHeight: 1080,
                raw:1,
                devArr:[
                    {
                        x:150,
                        y:430,
                        href: '#temphum'
                    },
                    {
                        x:250,
                        y:650,
                        href: '#yangan'
                    }
                ]
            }
        },
        mounted() {
            document.body.addEventListener('mousemove', this.mouseMoveHandel);
            document.body.addEventListener('mouseup', ()=>{this.buff = false;});
            window.onresize = ()=>{
                this.rawX = document.body.clientWidth/this.imgWidth;
                this.rawY = document.body.clientHeight/this.imgHeight;
                console.log(this.rawX, this.rawY);
            }
        },
        methods:{
            clickHandle(eve, item){
                this.buff = true;
                const curX = eve.clientX;
                const curY = eve.clientY;
                this.staX = curX;
                this.staY = curY;
                this.devX = this.curX;
                this.devY = this.curY;
                eve.stopPropagation();
                this.curDev = item;
            },
            mouseMoveHandel(eve){
                if(!this.buff) return;
                let endX = eve.clientX-this.staX+this.devX;
                let endY = eve.clientY-this.staY+this.devY;
                this.curDev.x = endX;
                this.curDev.y = endY;
            }
        }
    }
</script>

